import React, { useState, useEffect } from 'react';
import Navbar from './Navbar';

function AddDoctorPage() {
  const [docName, setDocName] = useState('');
  const [docEmail, setDocEmail] = useState('');
  const [docPhoneNo, setDocPhoneNo] = useState('');
  const [experience, setExperience] = useState('');
  const [bio, setBio] = useState('');
  const [consultationFee, setConsultationFee] = useState('');
  const [tagId, setTagId] = useState('');
  const [specialties, setSpecialties] = useState([]);
  const [selectedSpecialties, setSelectedSpecialties] = useState([]);
  const [qualifications, setQualifications] = useState([]);
  const [selectedQualifications, setSelectedQualifications] = useState([]);
  const [practices, setPractices] = useState([]);
  const [selectedPractices, setSelectedPractices] = useState([]);
  const [tags, setTags] = useState([]);
  const [message, setMessage] = useState('');

  // Fetch Tags
  useEffect(() => {
    fetch('http://localhost:8080/api/tags')
      .then(res => res.json())
      .then(data => setTags(data))
      .catch(err => console.error(err));
  }, []);

  // Fetch Specialities
  useEffect(() => {
    fetch('http://localhost:8080/api/specialities')
      .then(res => res.json())
      .then(data => setSpecialties(data))
      .catch(err => console.error(err));
  }, []);

  // Fetch Qualifications
  useEffect(() => {
    fetch('http://localhost:8080/api/qualifications')
      .then(res => res.json())
      .then(data => setQualifications(data))
      .catch(err => console.error(err));
  }, []);

  // Fetch Practices
  useEffect(() => {
    fetch('http://localhost:8080/api/practices')
      .then(res => res.json())
      .then(data => setPractices(data))
      .catch(err => console.error(err));
  }, []);

  const handleSpecialtiesChange = (e) => {
    const options = Array.from(e.target.selectedOptions, option => option.value);
    setSelectedSpecialties(options);
  };

  const handleQualificationsChange = (e) => {
    const options = Array.from(e.target.selectedOptions, option => option.value);
    setSelectedQualifications(options);
  };

  const handlePracticesChange = (e) => {
    const options = Array.from(e.target.selectedOptions, option => option.value);
    setSelectedPractices(options);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const doctorData = {
      docName,
      docEmail,
      docPhoneNo,
      experience: parseInt(experience),
      bio,
      consultationFee: parseFloat(consultationFee),
      tagId,
      specialtyIds: selectedSpecialties,
      qualificationIds: selectedQualifications,
      practiceIds: selectedPractices
    };

    try {
      const response = await fetch('http://localhost:8080/api/doctors', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(doctorData)
      });
      if (!response.ok) {
        throw new Error('Error adding doctor');
      }
      await response.json();
      setMessage('Doctor added successfully!');
      // Reset the form
      setDocName('');
      setDocEmail('');
      setDocPhoneNo('');
      setExperience('');
      setBio('');
      setConsultationFee('');
      setTagId('');
      setSelectedSpecialties([]);
      setSelectedQualifications([]);
      setSelectedPractices([]);
    } catch (error) {
      console.error(error);
      setMessage('Error adding doctor.');
    }
  };

  return (
    <div>
      <Navbar />
      <div className="container mx-auto p-8">
        <h2 className="text-3xl font-bold mb-6">Add Doctor</h2>
        {message && <p className="mb-4 text-green-600">{message}</p>}
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block mb-1">Doctor Name</label>
            <input
              type="text"
              value={docName}
              onChange={(e) => setDocName(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded"
              required
            />
          </div>
          <div>
            <label className="block mb-1">Email</label>
            <input
              type="email"
              value={docEmail}
              onChange={(e) => setDocEmail(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded"
              required
            />
          </div>
          <div>
            <label className="block mb-1">Phone No</label>
            <input
              type="text"
              value={docPhoneNo}
              onChange={(e) => setDocPhoneNo(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded"
              required
            />
          </div>
          <div>
            <label className="block mb-1">Experience (years)</label>
            <input
              type="number"
              value={experience}
              onChange={(e) => setExperience(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded"
              required
            />
          </div>
          <div>
            <label className="block mb-1">Bio</label>
            <textarea
              value={bio}
              onChange={(e) => setBio(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded"
              required
            />
          </div>
          <div>
            <label className="block mb-1">Consultation Fee</label>
            <input
              type="number"
              step="0.01"
              value={consultationFee}
              onChange={(e) => setConsultationFee(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded"
              required
            />
          </div>
          <div>
            <label className="block mb-1">Tag</label>
            <select
              value={tagId}
              onChange={(e) => setTagId(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded"
              required
            >
              <option value="">Select Tag</option>
              {tags.map((tag) => (
                <option key={tag.id} value={tag.id}>
                  {tag.tagName}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label className="block mb-1">Specialties</label>
            <select
              multiple
              value={selectedSpecialties}
              onChange={handleSpecialtiesChange}
              className="w-full p-2 border border-gray-300 rounded"
              required
            >
              {specialties.map((spec) => (
                <option key={spec.id} value={spec.id}>
                  {spec.specialityName}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label className="block mb-1">Qualifications</label>
            <select
              multiple
              value={selectedQualifications}
              onChange={handleQualificationsChange}
              className="w-full p-2 border border-gray-300 rounded"
              required
            >
              {qualifications.map((qual) => (
                <option key={qual.id} value={qual.id}>
                  {qual.qualName}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label className="block mb-1">Practices</label>
            <select
              multiple
              value={selectedPractices}
              onChange={handlePracticesChange}
              className="w-full p-2 border border-gray-300 rounded"
              required
            >
              {practices.map((practice) => (
                <option key={practice.practiceId} value={practice.practiceId}>
                  {practice.practiceName}
                </option>
              ))}
            </select>
          </div>
          <button type="submit" className="bg-blue-600 text-white p-2 rounded hover:bg-blue-700">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}

export default AddDoctorPage;
