import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

function LoginPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:8080/api/users/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });

      if (response.ok) {
        const data = await response.json();
        // Optionally store user data (e.g., in localStorage) for later use
        navigate('/home');
      } else {
        const errorData = await response.json();
        setError(errorData.error || 'Invalid credentials');
      }
    } catch (err) {
      console.error(err);
      setError('Something went wrong. Please try again.');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-blue-50 to-blue-100">
      {/* Logo Section */}
      <div className="mb-8">
        <div className="w-24 h-24 overflow-hidden rounded-full border-2 border-blue-600">
          <img 
            src="/logo.png" 
            alt="Practo Logo" 
            className="w-full h-full object-cover" 
          />
        </div>
      </div>

      {/* Provider Login Form */}
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded shadow-md w-96">
        <h2 className="text-3xl font-bold mb-6 text-center text-blue-800">Provider Login</h2>
        {error && <p className="text-red-500 mb-4 text-center">{error}</p>}

        <div className="mb-4">
          <label className="block mb-2 text-blue-700">Username</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="w-full border border-blue-300 p-2 rounded focus:outline-none focus:ring focus:border-blue-400"
            placeholder="Enter username"
            required
          />
        </div>

        <div className="mb-6">
          <label className="block mb-2 text-blue-700">Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full border border-blue-300 p-2 rounded focus:outline-none focus:ring focus:border-blue-400"
            placeholder="Enter password"
            required
          />
        </div>

        <button
          type="submit"
          className="w-full bg-blue-600 text-white p-2 rounded hover:bg-blue-700 transition duration-300"
        >
          Login as Provider
        </button>
      </form>

      {/* Patient Portal Section */}
      <div className="mt-8">
        <div className="bg-white p-6 rounded shadow-md w-96 text-center">
          <h3 className="text-xl font-semibold text-blue-800 mb-3">Patient Portal</h3>
          <p className="text-blue-600 mb-4">
            Looking for healthcare services? Explore our search features to find doctors and practices near you.
          </p>
          <Link
            to="/patient-home"
            className="inline-block bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition duration-300"
          >
            Go to Practo Search Page (Patient)
          </Link>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
